mapper.select:
select distinct prr.code as "receiptsRecordCode", /* 资金入账编码 */ pci.contract_no as "contractNo", /* 借款合同编号 */ pc.abbr_name as "cityName", /* 城市 */ pd.abbr_name as "distributorName", /* 渠道 */ (select name from plms_customer t where t.apply_code = pai.code and t.is_main = '1' and t.status = '1') as "customerName", /* 借款人 */ (select t2.abbr_name from plms_fund_side t1, plms_company t2 where t1.contract_code = pci.code and t1.status != '0' and t2.status != '0' and t1.company_code = t2.code) as "fundSideName",/* 资方 */ pad.total_quota as "totalQuota", /* 授信金额 */ (case when (prr.account_type = '00' ) then '宏获还款' when (prr.account_type = '01' ) then '资方还款' when (prr.account_type = '02' ) then '宏获保证金' end) as "accountTypeName",/*收款方*/ (case when (prr.fund_source = '01' ) then '客户' when (prr.fund_source = '02' ) then '宏获' when (prr.fund_source = '03' ) then '担保' end) as "fundSource",/*资金来源*/ prr.receipts_date as "receiptsDate",/*到账日期*/ prr.receipts_amount as "receiptsAmount",/*还款金额*/ prr.deal_status as "receiptsRecordStatusCode",/*状态编码*/ (case when (prr.deal_status = '00' ) then '待处理' when (prr.deal_status = '01' ) then '处理中' when (prr.deal_status = '02' ) then '处理成功' when (prr.deal_status = '03' ) then '处理失败' end) as "receiptsRecordStatusName"/*状态名称*/ from plms_contract_info pci, plms_apply_info pai, plms_distributor pd, plms_city pc, plms_account_detail pad, plms_receipts_record prr where pci.apply_code = pai.code and pai.distribute_code = pd.code and pd.city_no = pc.code and pci.code = pad.contract_code and pci.code = prr.contract_code and pci.status != '0' and pai.status != '0' and pd.status != '0' and pc.status != '0' and pad.status != '0' and prr.status != '0'
and pc.code=#{cityCode,jdbcType=VARCHAR}
AND pd.code IN
#{item.distributorCode}
and pd.code=#{distributorCode,jdbcType=VARCHAR}
and exists (select name from plms_customer t where t.apply_code = pai.code and t.name like concat('%', #{customerName,jdbcType=VARCHAR}, '%') )
and prr.receipts_date >= date_format(#{receiptsStartDate,jdbcType=VARCHAR}, '%Y/%m/%d')
and prr.receipts_date < date_add(date_format(#{receiptsEndDate,jdbcType=VARCHAR}, '%Y/%m/%d'),INTERVAL 1 DAY)
and prr.account_type = #{accountTypeCode,jdbcType=VARCHAR}
and prr.deal_status = #{receiptsRecordStatusCode,jdbcType=VARCHAR}
and prr.fund_source = #{fundSourceCode,jdbcType=VARCHAR}
order by prr.receipts_date desc

select pci.code as "contractCode",/*借款合同编码*/ pci.contract_no as "contractNo",/*借款合同编号*/ pc.abbr_name as "cityName",/*城市*/ pd.abbr_name as "distributorName",/*渠道*/ (select name from plms_customer t where t.apply_code = pai.code and t.is_main = '1') as "customerName",/*借款人*/ pci.credit_start_date as "creditStartDate",/*授信日期*/ pad.total_quota as "totalQuota",/*授信金额*/ pci.credit_period as "creditPeriod",/*授信期限*/ pad.fund_side_balance as "fundSideBalance",/*资方账户余额*/ pad.honghuo_bail_account_balance as "honghuoBailBalance",/*宏获保证金账户余额*/ pad.honghuo_balance as "honghuoBalance"/*宏获账户余额*/ from plms_contract_info pci, plms_apply_info pai, plms_distributor pd, plms_city pc, plms_account_detail pad where pci.apply_code = pai.code and pai.distribute_code = pd.code and pd.city_no = pc.code and pci.code = pad.contract_code and pci.status != '0' and pai.status != '0' and pd.status != '0' and pc.status != '0' and pad.status != '0'
and pc.code=#{cityCode,jdbcType=VARCHAR}
AND pd.code IN
#{item.distributorCode}
and pd.code=#{distributorCode,jdbcType=VARCHAR}
and exists (select name from plms_customer t where t.apply_code = pai.code and t.name like concat('%', #{customerName,jdbcType=VARCHAR}, '%') )
and pci.contract_no like concat('%', #{contractNo,jdbcType=VARCHAR}, '%')
order by pci.credit_start_date desc

mapper.update:
UPDATE plms_receipts_voucher
set
        status = #{status}
WHERE 1=1


    AND file_code = #{fileCode}


    AND receipts_record_code = #{receiptsRecordCode}

mapper.select:
select pbu.name as "uploadUser", prru.upload_time as "uploadTime", prru.status as "recordUploadStatusCode", (case when (prru.status = '01' ) then '校验中' when (prru.status = '02' ) then '校验成功' when (prru.status = '03' ) then '校验失败' end) as "recordUploadStatusName", (case when (prru.status = '01' ) then prru.file_code when (prru.status = '02' ) then prru.file_code when (prru.status = '03' ) then prru.error_file_code end) as "fileCode", (case when (prru.status = '01' ) then prru.file_name when (prru.status = '02' ) then prru.file_name when (prru.status = '03' ) then prru.error_file_name end) as "fileName"
from plms_backend_user pbu, plms_receipts_record_upload prru where pbu.status != '0' and prru.status != '00' and pbu.code = prru.upload_user
and prru.upload_user IN
#{item.code}
and prru.upload_user=#{uploadUser,jdbcType=VARCHAR}
and prru.upload_time >= date_format(#{uploadTimeStart,jdbcType=VARCHAR}, '%Y/%m/%d')
and prru.upload_time < date_add(date_format(#{uploadTimeEnd,jdbcType=VARCHAR}, '%Y/%m/%d'),INTERVAL 1 DAY)
and prru.status = #{statusCode,jdbcType=VARCHAR}
order by prru.upload_time desc
