mapper.select:
select distinct pci.code as "contractCode", /* 借款编码 */ pci.contract_no as "contractNo", /* 借款合同编号 */ pc.abbr_name as "cityName", /* 城市 */ pd.abbr_name as "distributorName", /* 渠道 */ (select name from plms_agent t where t.code = pai.agent_code and t.status != '0') "agentName", /* 业务经理 */ (select name from plms_customer t where t.apply_code = pai.code and t.is_main = '1' and t.status != '0') as "customerName", /* 借款人 */ (select product_name from plms_product t where t.apply_code = pai.code and t.status != '0') "productName",/* 产品名称 */ (case pai.lending_methods when '01' then '他证' when '02' then '收件收据' end) as "lendingMethodsName",/* 放款方式 */ (select t2.abbr_name from plms_fund_side t1, plms_company t2 where t1.contract_code = pci.code and t1.status != '0' and t2.status != '0' and t1.company_code = t2.code) as "fundSideName",/* 资方 */ (select t2.abbr_name from plms_guarantee_corporation t1, plms_company t2 where t1.contract_code = pci.code and t1.status != '0' and t2.status != '0' and t1.company_code = t2.code) as "guaranteeCorporationName",/* 担保公司 */ (select t2.abbr_name from plms_insurance_company t1, plms_company t2 where t1.contract_code = pci.code and t1.status != '0' and t2.status != '0' and t1.company_code = t2.code) as "insuranceCompanyName",/* 保险公司 */ pad.total_quota as "totalQuota", /* 授信金额 */ pad.principal "principal", (select sum(amount) from plms_repayment_schedule t where t.status != '00' and t.detail_code = pad.code ) as "repaymentAmount", /* 应还总额 */ date_format(pci.credit_start_date,'%Y-%m-%d %H:%i:%s') as "creditStartDate", /* 授信日期 */ date_format(pci.credit_end_date,'%Y-%m-%d %H:%i:%s') as "creditEndDate", /* 授信截止日 */ pci.credit_period as "creditPeriod", /* 授信期限 */ datediff(pci.credit_end_date,pci.credit_start_date) + 1 "creditDays",/* 授信天数 */ (select annualized_interest from plms_apply_interesting t where t.contract_code = pci.code and t.status != '0') as "annualizedInterest",/* 年化利率 */ (case when (ifnull(pad.principal,0) > 0 ) then (case when ifnull((select count(*) from plms_repayment_schedule prs where prs.detail_code = pad.code and prs.status = '03'),0) > 0 then '03' else '02' end) else '01' end) as "loanStatusCode", (case when (ifnull(pad.principal,0) > 0 ) then (case when ifnull((select count(*) from plms_repayment_schedule prs where prs.detail_code = pad.code and prs.status = '03'),0) > 0 then '逾期' else '正常还款中' end) else '已结清' end) as "loanStatusName", (case when (pain.is_interest_Instead = 0 ) then '0' when (pain.is_interest_Instead = 1 ) then '1' end) as "isInterestInstead", /* 利息是否代收代付 */ prs.total_period as "totalPeriod" from plms_contract_info pci, plms_apply_info pai, plms_distributor pd, plms_city pc, plms_account_detail pad, plms_repayment_schedule prs, plms_apply_interesting pain where pci.apply_code = pai.code and pai.distribute_code = pd.code and pd.city_no = pc.code and pci.code = pad.contract_code and prs.detail_code = pad.code and pain.contract_code = pci.code and pci.status != '0' and pai.status != '0' and pd.status != '0' and pc.status != '0' and pad.status != '0' and prs.status != '00' and pain.status != '0'
and pc.code=#{cityCode,jdbcType=VARCHAR}
and pad.confirmed=#{confirmed,jdbcType=VARCHAR}
AND pd.code IN
#{item.distributorCode}
and pd.code=#{distributorCode,jdbcType=VARCHAR}
and exists (select name from plms_customer t where t.apply_code = pai.code and t.name like concat('%', #{customerName,jdbcType=VARCHAR}, '%') )
and pci.credit_start_date >= date_format(#{creditStartDate,jdbcType=VARCHAR}, '%Y/%m/%d')
and pci.credit_start_date < date_add(date_format(#{creditEndDate,jdbcType=VARCHAR}, '%Y/%m/%d'),INTERVAL 1 DAY)
and exists (select name from plms_agent t where t.name like concat('%', #{agentName,jdbcType=VARCHAR}, '%') and t.distributor_code = pd.code)
and pci.code = #{contractCode,jdbcType=VARCHAR}
order by pci.credit_start_date desc

select
    count(*)
    from plms_repayment_schedule_detail_tmp prsdt
    where prsdt.status != '0'
      and prsdt.paid_code = #{paidCode,jdbcType=VARCHAR}
      and prsdt.repayment_date = #{repaymentDate,jdbcType=VARCHAR}

mapper.update:
UPDATE plms_repayment_schedule_detail_tmp  prsd
   SET prsd.total_period = #{totalPeriod,jdbcType=INTEGER} , prsd.current_period = #{currentPeriod,jdbcType=INTEGER}
WHERE prsd.status != '0'
 and prsd.code = #{code,jdbcType=VARCHAR}

UPDATE plms_repayment_schedule_detail  prsd
   SET prsd.total_period = #{totalPeriod,jdbcType=INTEGER} , prsd.current_period = #{currentPeriod,jdbcType=INTEGER}
WHERE prsd.status != '00'
 and prsd.code = #{code,jdbcType=VARCHAR}
