mapper.select:
SELECT rs.repayment_date as repaymentDate, rs.amount as amount, rs.principal AS principal, rs.interest AS interest, rs.overdue AS overdue, rs.code, rs.status, ifnull(rs.bail,0) as bail, ifnull(rs.bail_penalty,0) as bailPenalty FROM plms_repayment_schedule rs join plms_account_detail ac on rs.detail_code = ac.code
ac.contract_code = #{contractCode}
and rs.STATUS = #{status}
order by rs.repayment_date,rs.code

SELECT
sum(rs.amount)
FROM
plms_repayment_schedule rs join plms_account_detail ac on rs.detail_code = ac.code

    ac.contract_code = #{contractCode}

        and rs.STATUS = #{status}

SELECT rs.amount as repaymentAmount, rs.principal as repaymentPrincipal, rs.interest as repaymentInterest, rs.overdue AS repaymentOverdue, date_format(rs.repayment_date, '%Y-%m-%d') as repaymentDate, rs.status, rs.detail_code as detailCode, rs.code as code FROM plms_account_detail ad join plms_repayment_schedule rs on rs.detail_code = ad.code WHERE rs.status != '00' and ad.contract_code = #{contractCode}
and rs.repayment_date = #{repaymentDate}
order by rs.repayment_date

mapper.update:
update plms_repayment_schedule  set status = #{status}
WHERE (
        IFNULL(principal,0) + IFNULL(interest,0) + IFNULL(overdue, 0) + IFNULL(service_fee, 0) + IFNULL(service_fee_penalty, 0) + IFNULL(bail, 0) + IFNULL(bail_penalty, 0)
    ) - (
        IFNULL(repaymented_principal, 0)
        + IFNULL(repaymented_interest, 0)
        + IFNULL(repaymented_overdue, 0)
        + IFNULL(repaymented_service_fee, 0)
        + IFNULL(repaymented_service_fee_penalty, 0)

        + IFNULL(repaymented_bail, 0)
        + IFNULL(repaymented_bail_penalty, 0)
    ) = 0
and code = #{code}

update.foreach:
UPDATE plms_repayment_schedule t
SET t.total_period = #{bean.totalPeriod},
t.current_period = #{bean.currentPeriod}
WHERE
t.CODE = #{bean.code}

mapper.select:
SELECT a.id, a.`code`, a.`code` as repaymentScheduleCode, a.current_period as currentPeriod, a.total_period as totalPeriod, date_format(a.repayment_date,'%Y%m%d') as repaymentDate, a.amount, a.principal, a.interest, a.overdue, a.`status`, a.detail_code as detailCode, a.user_code as userCode, a.create_date as createDate, a.modify_date as modifyDate, a.repaymented_principal as repaymentedPrincipal, a.repaymented_interest as repaymentedInterest, a.repaymented_overdue as repaymentedOverdue, a.service_fee as serviceFee, a.service_fee_penalty as serviceFeePenalty, a.repaymented_service_fee as repaymentedServiceFee, a.repaymented_service_fee_penalty as repaymentedServiceFeePenalty, a.bail, a.bail_penalty as bailPenalty, a.repaymented_bail as repaymentedBail, a.repaymented_bail_penalty as repaymentedBailPenalty, e.mobile, e.name as customerName, d.distribute_code as distributorCode FROM plms_repayment_schedule a,plms_account_detail b,plms_contract_info c,plms_apply_info d,plms_customer e WHERE a.detail_code=b.code and b.contract_code=c.code and c.apply_code=d.code and d.code=e.apply_code and b.status!='0' and c.status!='0' and d.status!='0' and e.status!='0' and e.is_main='1' and a.status=#{status} and date_format(a.repayment_date,'%Y%m%d') IN
#{item}
and d.distribute_code in
#{item}

SELECT pa.email, pa.code as code, pa.bms_code as bmsCode FROM plms_agent pa WHERE pa.STATUS = #{status}
AND pa.code IN
#{item.agentCode}
